<?php
defined('BASEPATH') OR exit('No direct script access allowed');

class Readexcel extends CI_Controller {
 function __construct()    {        // Construct the parent class        parent::__construct();		$this->load->library("CI_Jwt"); 		$this->load->library("excel");         // Configure limits on our controller methods    }
 
	public function index()
	{		$file = 'assets/uploads/twd.xls';									$objPHPExcel = PHPExcel_IOFactory::load($file);									 									//get only the Cell Collection									$cell_collection = $objPHPExcel->getActiveSheet()->getCellCollection();									 									//extract to a PHP readable array format									foreach ($cell_collection as $cell) {										$column = $objPHPExcel->getActiveSheet()->getCell($cell)->getColumn();										$row = $objPHPExcel->getActiveSheet()->getCell($cell)->getRow();										$data_value = $objPHPExcel->getActiveSheet()->getCell($cell)->getValue();									 										//header will/should be in row 1 only. of course this can be modified to suit your need.										if ($row == 1) {											$header[$row][$column] = $data_value;										} else {											$arr_data[$row][$column] = $data_value;										} 									}															$queries = array();						foreach($arr_data as $key=>$obj)						{							 //print_a($obj,1);							 $ddo_code = $obj['D'];							 $school_id = 0 ;							 $school_rs = $this->db->query("select * from schools where school_code=?",array($ddo_code));							 if($school_rs->num_rows()>0)							 {								$school_id = $school_rs->row()->school_id;							 }							$queries[] = "insert into  tw_vendors set hostel_type														ehostel_id='".$obj['B']."',														hostel_name='".$obj['C']."',														ddo_code='".$obj['D']."',														ehostel_vendor_id='".$obj['E']."',														vendor_name='".$obj['F']."',														business_nature='".$obj['G']."',														tin_number='".$obj['H']."',														vendor_address='".$obj['I']."',														vendor_contact_number='".$obj['J']."',														supplier_name='".$obj['K']."',														supplier_contact_number='".$obj['L']."',														vendor_bank='".$obj['M']."',														vendor_bank_branch='".$obj['N']."',														vendor_bank_ifsc='".$obj['O']."',														vendor_account_number='".$obj['P']."',														supplier_aadhar_number ='".$obj['Q']."'";													}						echo implode(";<br>",$queries);die;	} 
}
